import { useState } from "react";
import { Routes, Route } from "react-router-dom";
import Nav from "../components/Nav";
import Popular from "../pages/Popular";
import Upcoming from "../pages/Upcoming";
import Details from "../pages/Details";
import Searched from "../pages/Searched";
import TopRated from "../pages/TopRated";
import LoginPage from "../components/LoginPage";
import Dashboard from "../pages/Dashboard";
import RegisterPage from "../components/RegisterPage";

function Main() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State untuk melacak status login

  // Callback function untuk mengubah status login
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Callback function untuk mengubah status register
  const handleRegister = () => {
    setIsLoggedIn(true);
  };

  return (
    <>
      <Nav isLoggedIn={isLoggedIn} />{" "}
      {/* Mengirimkan status login ke komponen Nav */}
      <Routes>
        <Route index element={<Popular />} />
        <Route element={<Upcoming />} path="/upcoming" />
        <Route element={<TopRated />} path="/toprated" />
        <Route element={<Details />} path="/details/:id" />
        <Route element={<Searched />} path="/search/:title" />
        {isLoggedIn ? (
          <>
            <Route element={<Dashboard />} path="/dashboard" />
          </>
        ) : (
          <>
            <Route
              element={<LoginPage handleLogin={handleLogin} />}
              path="/login"
            />
            <Route
              element={<RegisterPage handleRegister={handleRegister} />}
              path="/register"
            />
          </>
        )}
      </Routes>
    </>
  );
}

export default Main;
